import { Component } from '@angular/core';
import { CuentaService } from 'src/app/services/cuenta.service';

@Component({
  selector: 'app-cuenta',
  templateUrl: './cuenta.component.html',
  styleUrls: ['./cuenta.component.css']
})
export class CuentaComponent {
  cuentaId: number = 0; // 
  saldo: number = 0;
  monto: number = 0;
  mensaje: string = '';

  constructor(private cuentaService: CuentaService) {}

  obtenerSaldo() {
    if (!this.cuentaId || this.cuentaId <= 0) {
      this.mensaje = 'Ingrese un ID de cuenta vÃ¡lido.';
      return;
    }

    this.cuentaService.getSaldo(this.cuentaId).subscribe({
      next: (response) => {
        this.saldo = response.saldo; // ðŸ“Œ Accedemos a la propiedad correcta
        this.mensaje = 'Saldo obtenido correctamente.';
      },
      error: (error) => {
        console.error('Error al obtener saldo:', error);
        this.mensaje = 'Error al consultar saldo. Verifique el ID de la cuenta.';
      }
    });
  }

  depositar() {
    this.cuentaService.depositar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `DepÃ³sito de $${this.monto} realizado con Ã©xito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el depÃ³sito';
        console.error(error);
      }
    );
  }

  retirar() {
    this.cuentaService.retirar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `Retiro de $${this.monto} realizado con Ã©xito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el retiro';
        console.error(error);
      }
    );
  }
}


