import { Component } from '@angular/core';
import { CuentaService } from 'src/app/services/cuenta.service';

@Component({
  selector: 'app-cuenta',
  templateUrl: './cuenta.component.html',
  styleUrls: ['./cuenta.component.css']
})
export class CuentaComponent {
  cuentaId: number = 0; // 
  saldo: number = 0;
  monto: number = 0;
  mensaje: string = '';

  constructor(private cuentaService: CuentaService) {}

  obtenerSaldo() {
    if (!this.cuentaId || this.cuentaId <= 0) {
      this.mensaje = 'Ingrese un ID de cuenta v√°lido.';
      return;
    }

    this.cuentaService.getSaldo(this.cuentaId).subscribe({
      next: (response) => {
        this.saldo = response.saldo; // üìå Accedemos a la propiedad correcta
        this.mensaje = 'Saldo obtenido correctamente.';
      },
      error: (error) => {
        console.error('Error al obtener saldo:', error);
        this.mensaje = 'Error al consultar saldo. Verifique el ID de la cuenta.';
      }
    });
  }

  depositar() {
    this.cuentaService.depositar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `Dep√≥sito de $${this.monto} realizado con √©xito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el dep√≥sito';
        console.error(error);
      }
    );
  }

  retirar() {
    this.cuentaService.retirar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `Retiro de $${this.monto} realizado con √©xito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el retiro';
        console.error(error);
      }
    );
  }
}
<div class="container mt-5">
  <div class="card shadow-lg p-4">
    <h2 class="text-center text-primary mb-4">Gesti√≥n de Cuentas Bancarias</h2>

    <!-- Ingreso del ID de la cuenta -->
    <div class="mb-3">
      <label for="cuentaId" class="form-label">ID de Cuenta:</label>
      <input type="number" [(ngModel)]="cuentaId" class="form-control" id="cuentaId" placeholder="Ingrese el ID de la cuenta">
    </div>

    <!-- Botones de acciones -->
    <div class="d-flex justify-content-between">
      <button class="btn btn-primary" (click)="getSaldo()">Consultar Saldo</button>
      <button class="btn btn-success" (click)="depositar()">Depositar</button>
      <button class="btn btn-danger" (click)="retirar()">Retirar</button>
    </div>

    <!-- Resultado de la consulta -->
    <div class="mt-4">
      <h4 *ngIf="saldo !== null" class="text-success">Saldo: ${{ saldo | number }}</h4>
      <p *ngIf="mensaje" class="text-danger">{{ mensaje }}</p>
    </div>
  </div>
</div>
