import { Component } from '@angular/core';
import { CuentaService } from 'src/app/services/cuenta.service';

@Component({
  selector: 'app-cuenta',
  templateUrl: './cuenta.component.html',
  styleUrls: ['./cuenta.component.css']
})
export class CuentaComponent {
  cuentaId: number = 2; // Puedes cambiarlo seg√∫n el ID de prueba
  saldo: number = 0;
  monto: number = 0;
  mensaje: string = '';

  constructor(private cuentaService: CuentaService) {}

  obtenerSaldo() {
    if (!this.cuentaId || this.cuentaId <= 0) {
      this.mensaje = 'Ingrese un ID de cuenta v√°lido.';
      return;
    }

    this.cuentaService.getSaldo(this.cuentaId).subscribe({
      next: (response) => {
        this.saldo = response.saldo; // üìå Accedemos a la propiedad correcta
        this.mensaje = 'Saldo obtenido correctamente.';
      },
      error: (error) => {
        console.error('Error al obtener saldo:', error);
        this.mensaje = 'Error al consultar saldo. Verifique el ID de la cuenta.';
      }
    });
  }

  depositar() {
    this.cuentaService.depositar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `Dep√≥sito de $${this.monto} realizado con √©xito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el dep√≥sito';
        console.error(error);
      }
    );
  }

  retirar() {
    this.cuentaService.retirar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `Retiro de $${this.monto} realizado con √©xito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el retiro';
        console.error(error);
      }
    );
  }
}
<div class="container">
  <h2>Gesti√≥n de Cuenta</h2>

  <div class="mb-3">
    <label for="cuentaId" class="form-label">ID de Cuenta:</label>
    <input type="number" [(ngModel)]="cuentaId" class="form-control" id="cuentaId">
  </div>

  <p *ngIf="saldo !== null">Saldo: ${{ saldo | number }}</p>


  <button (click)="obtenerSaldo()" class="btn btn-primary">Consultar Saldo</button>

  <div class="mt-3">
    <label>Monto:</label>
    <input type="number" [(ngModel)]="monto" class="form-control">
  </div>

  <button (click)="depositar()" class="btn btn-success mt-2">Depositar</button>
  <button (click)="retirar()" class="btn btn-danger mt-2">Retirar</button>

  <p class="mt-3 text-info">{{ mensaje }}</p>
