import { Component } from '@angular/core';
import { CuentaService } from 'src/app/services/cuenta.service';

@Component({
  selector: 'app-cuenta',
  templateUrl: './cuenta.component.html',
  styleUrls: ['./cuenta.component.css']
})
export class CuentaComponent {
  cuentaId: number = 1; // Puedes cambiarlo según el ID de prueba
  saldo: number = 0;
  monto: number = 0;
  mensaje: string = '';

  constructor(private cuentaService: CuentaService) {}

  obtenerSaldo() {
    this.cuentaService.getSaldo(this.cuentaId).subscribe(
      (saldo) => {
        this.saldo = saldo;
        this.mensaje = `Saldo actualizado: $${saldo}`;
      },
      (error) => {
        this.mensaje = 'Error al obtener saldo';
        console.error(error);
      }
    );
  }

  depositar() {
    this.cuentaService.depositar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `Depósito de $${this.monto} realizado con éxito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el depósito';
        console.error(error);
      }
    );
  }

  retirar() {
    this.cuentaService.retirar(this.cuentaId, this.monto).subscribe(
      () => {
        this.mensaje = `Retiro de $${this.monto} realizado con éxito.`;
        this.obtenerSaldo();
      },
      (error) => {
        this.mensaje = 'Error al realizar el retiro';
        console.error(error);
      }
    );
  }
}
